<?phprequire_once('inc.const.php');function redirect($_extra = 'index.php') {	$host  = $_SERVER['HTTP_HOST'];	$uri   = rtrim(dirname($_SERVER['PHP_SELF']), '/\\');	header("Location: http://$host$uri/$_extra");	exit;}/** * Checks for validity of a id for a sql-table * @param int $id id in question * @return boolean true, if the passed variable can be used as sql-table-id */function isValidId($id) {	return $id !== null && is_numeric($id) && $id >= 0;}function pwhash($_str) {	return hash('whirlpool', $_str); }function pprint($_var) {	if(!Constants::DEBUGGING) {		die('please leave debug-mode!');	}	echo '<pre>';	print_r($_var);	echo '</pre>';}/** * Tries to retrieve a field from an array * @param $_key key for the array * @param array $_array array to retrieve the value from * @param boolean $_trim trim the result before returning (default: false) * @return Ambigous <NULL, unknown> the value, if any is found, NULL, if the key is not specified */function tryGet($_key, $_array, $_trim = false) {	$result = is_array($_array) && array_key_exists($_key, $_array) && isset($_array[$_key]) ? $_array[$_key] : null;	if($_trim) {		$result = trim($result);	} 	return $result;}?>